const Total = (props) => {
  const { parts } = props.course;
  const reducer = (accumulator, currentValue) =>
    accumulator + currentValue.exercises;
  const total = parts.reduce(reducer, 0);
  return <p>Total of {total} exercises</p>;
};

export default Total;
