const Total = (props) => {
  const { parts } = props.course;
  const { exercises: ex1 } = parts[0];
  const { exercises: ex2 } = parts[1];
  const { exercises: ex3 } = parts[2];
  return (
    <p>
      Number of exercises
      {parts.reduce(
        (accumulator, currentValue) => accumulator + currentValue.exercises
      )}
    </p>
  );
};

export default Total;
