import React, { useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" },
  ]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");

  const handleNameTextboxChange = (event) => {
    setNewName(event.target.value);
  };

  const handleNumberTextboxChange = (event) => {
    setNewNumber(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const checkIfNameIsFound = persons.find(
      (person) => person.name === newName
    );
    if (checkIfNameIsFound === undefined) {
      setPersons(persons.concat({ name: newName, number: newNumber }));
    } else {
      alert(`${checkIfNameIsFound.name} is already added to the phonebook`);
    }
    setNewName("");
    setNewNumber("");
  };
  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={handleSubmit}>
        <div>
          name: <input value={newName} onChange={handleNameTextboxChange} />
        </div>
        <div>
          number:{" "}
          <input value={newNumber} onChange={handleNumberTextboxChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map((person) => (
        <p key={person.name}>
          {person.name} {person.number}
        </p>
      ))}
    </div>
  );
};

export default App;
